mapper.insert:
		
			SELECT task_task_no_seq.nextval FROM DUAL;
		
		INSERT INTO task_no, title, content, startDate, endDate, state, user_no, category_no
		VALUES(#{taskNo},#{title},#{content},#{startDate}, #{endDate},#{state},#{userNo},#{categoryNo});

mapper.update:
UPDATE task t
SET t.title = #{title}, t.content =#{content}, t.startDate=#{startDate}, t.endDate=#{endDate},t.state=#{state},
	t.category_no = #{categoryNo}
